(20, 'wcpay_instant_deposits_gtm_2021', 'marketing', 'en_US', 'Get paid within minutes  Instant Deposits for WooCommerce Payments', 'Stay flexible with immediate access to your funds when you need them  including nights, weekends, and holidays. With <a href=\"https://woocommerce.com/products/woocommerce-payments/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_instant_deposits\">WooCommerce Payments\'</a> new Instant Deposits feature, youre able to transfer your earnings to a debit card within minutes.', '{}', 'pending', 'woocommerce.com', '2021-11-05 09:02:46', NULL, 0, 'plain', '', 0, 'info'),
(21, 'google_listings_and_ads_install', 'marketing', 'en_US', 'Drive traffic and sales with Google', 'Reach online shoppers to drive traffic and sales for your store by showcasing products across Google, for free or with ads.', '{}', 'pending', 'woocommerce.com', '2021-11-05 09:02:46', NULL, 0, 'plain', '', 0, 'info'),
